import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super()
    this.handleClickOne = this.handleClickOne.bind(this)
  }

state = {
  clickOne: 0,
}

//checa se o numero é par e adiciona a cor verde ou branca
//como não estamos utilizando um arquivo .css faremos inline dentro do return
getButtonColor(num) {
  return num % 2 === 0 ? 'green' : 'white';
}

handleClickOne(){
  this.setState(({ clickOne }) => ({
    clickOne: clickOne + 1,
  }))
}

  render() {
    const { clickOne } = this.state
    return (
      <button onClick={this.handleClickOne}>{ clickOne }</button>
    );
  }
}

export default App;
