import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super()
    this.handleClick = this.handleClick.bind(this)
  }

state = {
  clickNumber: 0,
}

handleClick(){
  this.setState(({ clickNumber }) => ({
    clickNumber: clickNumber + 1,
  }))
}

  render() {
    const { clickNumber } = this.state
    return (
      <button onClick={this.handleClick}>{ clickNumber }</button>
    );
  }
}

export default App;
